import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { Switch } from 'react-router-dom';
import { Link } from 'react-router-dom';
import '../css/presentation.css'; 

const Presentation = () => {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [tel, setTel] = useState('');
  const [ubicacion, setUbication] = useState('');
  const [image, setImage] = useState(null);

  const handleImageChange = (event) => {
    const selectedImage = event.target.files[0];
    setImage(URL.createObjectURL(selectedImage));
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleTelChange = (event) => {
    setTel(event.target.value);
  };

  const handleUbicationChange = (event) => {
    setUbication(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // Aquí iría tu lógica de registro con el servidor
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <div className='Selperfil'>
        <input
          type="file"
          accept="images/*"
          onChange={handleImageChange}
          style={{ display: 'none' }}
          id="upload-file"
        />
        <label htmlFor="upload-file" className="upload-label">
          {image ? (
            <img src={image} alt="Profile" className="profile-image" />
          ) : (
            <div className='ImgPerf'>
              <p>Selecciona una imagen de perfil</p>
            </div>
          )}
        </label>
      </div>
  
      <div className='Form'>
        <form onSubmit={handleSubmit}>
          <div className='email'>
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={handleEmailChange}
              required
            />
          </div>
          <div>
            <label htmlFor="password">Contraseña</label> 
            <input
              type="password"
              id="password"
              value={password}
              onChange={handlePasswordChange}
              required
            />
            <label htmlFor="tel">Teléfono</label> 
            <input
              type="tel"
              id="tel"
              value={tel}
              onChange={handleTelChange}
              required
            />
            <label htmlFor="ubicacion">Dirección</label> 
            <input
              type="text"
              id="ubicacion"
              value={ubicacion}
              onChange={handleUbicationChange}
              required
            />
          </div>
        </form>
      </div>
      
      <h3>¿Qué se considera?</h3>
      <div className='ButtClass'>
        <button><Link to="/logprov">Proveedor</Link></button>
        <button><Link to="/logcom">Comerciante</Link></button>
      </div>
      <button type="submit" className='Volver'>Registrarse</button>
    </div>
  );
}

export default Presentation;
