import React, { useState } from "react";
import '../css/logprov.css'; 

const LogProv = ({goToPresentation, goToFinal}) => {
  const [tel, setOtroTel] = useState('');
  const [document, setCuitDoc] = useState('');
  const [Text, setPresent] = useState('');
  const [linkweb, setWeb] = useState('');
  const [linkInst, setIg] = useState('');
  const [linkFace, setFace] = useState('');
  const [link, setCatalogo] = useState('');
  const [image, setImage] = useState(null);
  const [aceptado, setAceptado] = useState(false);

  //  recordar que lo que esta entre parentesis es de como se encuentra inicialmente, por ejempl '' es que esta vacio, y su funcion sera llenarlo con caracteres
 
  const handleImageChange = (event) => {
    const selectedImage = event.target.files[0];
    setImage(URL.createObjectURL(selectedImage));
   };

  const handleOtroTelChange  = (event) => {
    setOtroTel(event.target.value)
  };

  const handleCuitOrDocChange  = (event) => {
    setCuitDoc(event.target.value)
  };

  const handlePresentChange  = (event) => {
    setPresent(event.target.value)
 };

  const handleWebChange  = (event) => {
    setWeb(event.target.value)
  };

  const handleIgChange  = (event) => {
    setIg(event.target.value)
  };

  const handleFaceChange  = (event) => {
    setFace(event.target.value)
  };
   
  const handleCatalogoChange  = (event) => {
  setCatalogo(event.target.value)};

  const handleCheckboxChange = (e) => {
    setAceptado(e.target.checked)};

  // const handleSubmit = (event) => {
  //   event.preventDefault()};

/////////////////////////////////////////////////////////////////////
// para back
  const handleSubmit = async (event) => {
    event.preventDefault();
    if (aceptado) {
      try {
        const formData = new FormData();
        formData.append('tel', tel);
        formData.append('document', document);
        formData.append('presentacion', Text);
        formData.append('linkWeb', linkweb);
        formData.append('linkInstagram', linkInst);
        formData.append('linkFacebook', linkFace);
        formData.append('linkCatalogo', link);
        // Agrega aquí el campo para la imagen si decides enviarla
        // formData.append('image', image);

        const response = await fetch('/registro', {
          method: 'POST',
          body: formData // Aquí enviamos el formulario como FormData
        });
        const data = await response.json();
        console.log(data);
        // Aquí puedes redirigir al usuario o mostrar un mensaje de éxito
      } catch (error) {
        console.error('Error:', error);
      }
    } else {
      // Mostrar mensaje de que debe aceptar los términos y condiciones
    }
  };

  //////////////////////////////////////////////////////////////

    return (
        <div className="RestForm">
          <form onSubmit={handleSubmit}>
          <label htmlFor="otrotel">Otro Telefono </label> 
            <input
              type="tel"
              id="otrotel"
              value={tel}
              onChange={handleOtroTelChange}
              required
            />
            <label htmlFor="cuitordoc">Cuit O Documento </label> 
            <input
              type="document"
              id="cuitordoc"
              value={document}
              onChange={handleCuitOrDocChange}
              required
            />
            <label htmlFor="present">Presentacion</label> 
            <input
              type="text"
              id="present"
              value={Text}
              onChange={handlePresentChange}
              required
            />
            <label htmlFor="linkweb">Sitio web</label>
            <input
              type="link"
              id="linkweb"
              value={linkweb}
              onChange={handleWebChange }
              required
            />
            <label htmlFor="linkInst">Instagram</label> 
            <input
              type="link"
              id="linkInst"
              value={linkInst}
              onChange={handleIgChange }
              required
            />
            <label htmlFor="linkFace">Facebook</label> 
            <input
              type="link"
              id="linkFace"
              value={linkFace}
              onChange={handleFaceChange }
              required
            />
          </form>
          <div className="Select">
          <h2>¿Que productos y servicios ofrece?</h2>
            <div className="SelectProd"> 
              <button onClick={() => handleSeleccion('Venta al público')}>Venta al público</button>
              <button onClick={() => handleSeleccion('Servicio a domicilio')}>Servicio a domicilio</button>
              <button onClick={() => handleSeleccion('Golosinas')}>Golosinas</button>
              <button onClick={() => handleSeleccion('Cigarrillos')}>Cigarrillos</button>
              <button onClick={() => handleSeleccion('Bebidas')}>Bebidas</button>
              <button onClick={() => handleSeleccion('Galletiteria')}>Galletiteria</button>
              <button onClick={() => handleSeleccion('Helados y Hielo')}>Helados y Hielo</button>
              <button onClick={() => handleSeleccion('Productos congelados')}>Productos congelados</button>
              <button onClick={() => handleSeleccion('Lacteos')}>Lacteos</button>
              <button onClick={() => handleSeleccion('Fiambres')}>Fiambres</button>
              <button onClick={() => handleSeleccion('Productos de limpieza')}>Productos de limpieza</button>
              <button onClick={() => handleSeleccion('Productos de higiene personal')} className="ProdHig">Productos de higiene</button>
             {/* estos seran utilizados para darle la logica para cada boton idividualmente */}

            </div>
            <form onSubmit={handleSubmit}>
             <label htmlFor="linkcat">Link Catalogo</label> 
             <input
              type="link"
              id="linkcat"
              value={link}
              onChange={handleCatalogoChange }
              required
             />
            </form>
            
            <h2>¿No tienes un catalogo online? Contacta con nosotros en www.Alliance.com.ar, Crece en el mundo del mercado con el desarrollo web de tu negocio</h2>
            
            {/* aqui se agrega la seccion de fotos que agregue el usuario */}
            <div className="Fotos">
              <h2>Agregar fotos</h2>

              <div className='Selperfil'>
               {/* Input de archivo para seleccionar la imagen */}
               <input
                type="file"
                accept="iamges/*"
                onChange={handleImageChange}
                style={{ display: 'none' }}
                id="upload-file"
               />
               
      
               <label htmlFor="upload-file" className="upload-label">
                {image ? (
                <img src={image} alt="Profile" className="profile-image" />
                ) : ( <div className='ImgPerf'>
                <p></p></div>
                )}
               </label>
              </div>

            </div>
          </div>
          <div className="FinAndTerm">
            <form className="Terms">
            <input type="checkbox" id="terminos" onChange={handleCheckboxChange} />
            <label htmlFor="terminos">Acepto los términos y condiciones</label></form>
            <button disabled={!aceptado} onClick={goToFinal} className='Fin' >Finalizar</button>
          </div>
          <div className='ButtonAcces'><button onClick={goToPresentation} className='InitAccess' >Volver</button></div>
          {/* este boton representara el boton de finalizar, en ambos casos */}
        </div>
        
    );
}
export default LogProv;
