import React, { useState } from 'react';
import '../css/login.css'; 

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ email, password })
      });
      const data = await response.json();
      if (response.ok) {
        console.log('Login successful:', data);
        localStorage.setItem('user', JSON.stringify(data));
        // Redirige al usuario a la página de presentación
        window.location.href = '/presentation'; // Cambio a window.location.href para redirigir
      } else {
        setError(data.message || 'Login failed');
      }
    } catch (error) {
      console.error('Error:', error);
      setError('An error occurred. Please try again.');
    }
  };

  return (
    <div>
      <div className='Title'>
        <h1>Alliance</h1>
      </div>
      <div className='Body'>
        <div className='Inicio'><h1>Iniciar Sesión</h1></div>
        <form onSubmit={handleSubmit}>
          <div className='email'>
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={handleEmailChange}
              required
            />
          </div>
          <div>
            <label htmlFor="password">Contraseña </label> 
            <input
              type="password"
              id="password"
              value={password}
              onChange={handlePasswordChange}
              required
            />
          </div>
          {error && <p className="error">{error}</p>}
          <div className='ButtonAcces'>
            <button type="submit" className='InitSes'>Iniciar Sesión</button>
          </div>
        </form>
      </div>
      <div className='Access'><h2>¿Olvidaste la contraceña?</h2>
        <div className='ButtonAcces'><button onClick={() => window.location.href = '/presentation'} className='InitAccess' >¿Primera vez en Alliance? Accede ahora</button></div>
      </div>
    </div>
  );
}

export default Login;
