import React, { useState } from 'react';

import '../css/presentation.css'; 



const Presentation = ({ goToLogProv }) => {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [tel, setTel] = useState('');
  const [ubicacion, setUbication] = useState('');
  const [image, setImage] = useState(null);

  const handleImageChange = (event) => {
    const selectedImage = event.target.files[0];
    setImage(URL.createObjectURL(selectedImage));
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleTelChange = (event) => {
    setTel(event.target.value);
  };

  const handleUbicationChange = (event) => {
    setUbication(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Aquí puedes manejar la lógica para enviar los datos del formulario
    console.log('Correo electrónico:', email);
    console.log('Contraseña:', password);
    console.log('Telefono', tel);
    console.log('Direccion', ubicacion );
    // También puedes enviar los datos a un servidor o realizar otras acciones
  };

  return (
    <div>
      <div className='Selperfil'>
      {/* Input de archivo para seleccionar la imagen */}
      <input
        type="file"
        accept="iamges/*"
        onChange={handleImageChange}
        style={{ display: 'none' }}
        id="upload-file"
      />
      
      <label htmlFor="upload-file" className="upload-label">
        {image ? (
          <img src={image} alt="Profile" className="profile-image" />
        ) : ( <div className='ImgPerf'>
          <p>Selecciona una imagen de perfil</p></div>
        )}
      </label>

     </div>
  
      <div className='Form'>
      <form onSubmit={handleSubmit}>
          <div className='email'>
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={handleEmailChange}
              required
            />
          </div>
          <div>
            <label htmlFor="password">Contraseña </label> 
            <input
              type="password"
              id="password"
              value={password}
              onChange={handlePasswordChange}
              required
            />
            <label htmlFor="tel">Telefono </label> 
            <input
              type="tel"
              id="tel"
              value={tel}
              onChange={handleTelChange}
              required
            />
            <label htmlFor="Ubicacion">Direccion </label> 
            <input
              type="Ubicacion"
              id="Ubicacion"
              value={ubicacion}
              onChange={handleUbicationChange}
              required
            />
           
          </div>
        </form>
      </div>
      


      <div><h3>¿Que se considera?</h3></div>
      {/* <button onClick={goToLogCom}>Comerciante</button>  */}
      {/* este boton sera usado para el de comerciantes goToLoCom */}
      <div className='ButtClass'>
       <button onClick={goToLogProv}>Proveedor</button>
       <button onClick={goToLogCom}>Comerciante</button> </div>
    </div>
  );
}

export default Presentation;
