import React, { useState } from 'react';
import '../css/login.css'; 

const Login = ({ goToPresentation }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  // const handleSubmit = (event) => {
  //   event.preventDefault();
  //   // Aquí puedes manejar la lógica para enviar los datos del formulario
  //   console.log('Correo electrónico:', email);
  //   console.log('Contraseña:', password);
  //   // También puedes enviar los datos a un servidor o realizar otras acciones
  // };

  /////////////////
  // para conectar back
  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ email, password })
      });
      const data = await response.json();
      if (response.ok) {
        console.log('User registered successfully:', data);
      } else {
        console.error('Registration failed:', data.message);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };
  
  ////////////////

  return (
    <div>
      <div className='Title'>
        <h1>Alliance</h1>
      </div>
      
      <div className='Body'>
        <div className='Inicio'><h1>Iniciar Sesión</h1></div>
        <form onSubmit={handleSubmit}>
          <div className='email'>
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={handleEmailChange}
              required
            />
          </div>
          <div>
            <label htmlFor="password">Contraseña </label> 
            <input
              type="password"
              id="password"
              value={password}
              onChange={handlePasswordChange}
              required
            />
          </div>
        </form>
        <div className='ButtonAcces'><button type="submit" className='InitSes'>Iniciar Sesión</button></div>
        
      </div>
      <div className='Access'><h2>¿Olvidaste la contraceña?</h2>
      <div className='ButtonAcces'><button onClick={goToPresentation} className='InitAccess' >¿Primera vez en Alliance? Accede ahora</button></div>
      </div>
    </div>
    
  );
}

export default Login;
