import React, { useEffect, useState } from 'react';

const Busqueda = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [users, setUsers] = useState([]);

    // Función para obtener todos los usuarios
    const fetchAllUsers = async () => {
        try {
            const response = await fetch('http://localhost:3000/api/users'); // Asegúrate de que esta URL es correcta
            const data = await response.json();
            setUsers(data);
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };
    
    const handleSearch = async () => {
        try {
            const response = await fetch(`http://localhost:3000/api/users/search?name=${searchTerm}`); // Asegúrate de que esta URL es correcta
            const data = await response.json();
            setUsers(data);
        } catch (error) {
            console.error('Error searching users:', error);
        }
    };

    // Obtener todos los usuarios al cargar la página
    useEffect(() => {
        fetchAllUsers();
    }, []);

    return (
        <div>
            <div>
                <input 
                    type="text" 
                    placeholder="Search users..." 
                    value={searchTerm} 
                    onChange={(e) => setSearchTerm(e.target.value)} 
                />
                <button onClick={handleSearch}>Search</button>
            </div>
            <div>
                {users.map(user => (
                    <div key={user.id}>
                        <h2>{user.name}</h2>
                        <h4>{user.description}</h4>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Busqueda;
