import React, { useState, useEffect } from 'react';
import Home from './components/Login';
import Presentation from './components/Presentation';
import Loading from './components/Loading';
import LogProv from './components/LogProv';
import LogCom from './components/LogCom'
import Final from './components/Final';

function App() {
  const [page, setPage] = useState('loading');

  useEffect(() => {
    const timer = setTimeout(() => {
      setPage('login');
    }, 2000);
    // despues de la pantalla de carga, se dirige directamente al home

    return () => clearTimeout(timer)
  }, []);
  // esta funcion return, corta el tiempo de la presentacion

  const goToPresentation = () => {
    setPage('presentation');
  };

  const goToLogCom= () => {
    setPage('logcom');
  };

  const goToLogProv = () =>{
    setPage('logprov');
  }
  const goToFinal = () => {
    setPage('final')
  }

  return (
    <div>
      {page === 'loading' && <Loading />}
      {page === 'login' && <Home goToPresentation={goToPresentation} />} 
      {/* EL gotopresentation se utiliza para la funcion del boton, lo mismo con go to home. */}
      {page === 'presentation' && <Presentation goToLogProv={goToLogProv} goToLogCom={goToLogCom}/>}
      {page === 'logprov'  && <LogProv goToPresentation={goToPresentation} goToFinal={goToFinal} /> }
      {page === 'logcom'  && <LogCom goToPresentation={goToPresentation} goToFinal={goToFinal}/> }
      {page === 'final'  && <Final/> }
    </div>
  );
}

export default App;
