import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Switch } from 'react-router-dom';

import "../css/logprov.css";

const LogCom = () => {
  const [seleccion, setSeleccion] = useState('');
  const handleSeleccion = (event) => {
    const opcionSeleccionada = event.target.value;
    console.log("Seleccionaste: " + opcionSeleccionada);
    setSeleccion(opcionSeleccionada);
  };

  const handleImageChange = (event) => {
    const selectedImage = event.target.files[0];
    setImage(URL.createObjectURL(selectedImage));
  };

  const [tel, setOtroTel] = useState('');
  const [document, setCuitDoc] = useState('');
  const [Text, setPresent] = useState('');
  const [linkweb, setWeb] = useState('');
  const [linkInst, setIg] = useState('');
  const [linkFace, setFace] = useState('');
  const [image, setImage] = useState(null);
  const [aceptado, setAceptado] = useState(false);

  const handleOtroTelChange = (event) => {
    setOtroTel(event.target.value);
  };

  const handleCuitOrDocChange = (event) => {
    setCuitDoc(event.target.value);
  };

  const handlePresentChange = (event) => {
    setPresent(event.target.value);
  };

  const handleWebChange = (event) => {
    setWeb(event.target.value);
  };

  const handleIgChange = (event) => {
    setIg(event.target.value);
  };

  const handleFaceChange = (event) => {
    setFace(event.target.value);
  };

  const handleCheckboxChange = (e) => {
    setAceptado(e.target.checked);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (aceptado) {
      try {
        const formData = {
          tel,
          document,
          presentacion: Text,
          linkWeb: linkweb,
          linkInstagram: linkInst,
          linkFacebook: linkFace,
          image
        };

        const response = await fetch('http://localhost:3000/registro/comerciante', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(formData)
        });
        const data = await response.json();
        console.log(data);
        // Aquí puedes redirigir al usuario o mostrar un mensaje de éxito
      } catch (error) {
        console.error('Error:', error);
      }
    } else {
      // Mostrar mensaje de que debe aceptar los términos y condiciones
    }
  };

  return (
    <div className="RestForm">
      <form onSubmit={handleSubmit}>
        {/* Inputs y formularios */}
      </form>

      <div className="ButtonAcces">
        <Link to="/presentation" className="InitAccess">Volver</Link>
      </div>
      <div className="FinAndTerm">
        <form className="Terms">
          <input type="checkbox" id="terminos" onChange={handleCheckboxChange} />
          <label htmlFor="terminos">Acepto los términos y condiciones</label>
        </form>
        <button disabled={!aceptado} className='Fin' >Finalizar</button>
      </div>
    </div>
  );
}

export default LogCom;
