import React, { useState, useEffect } from 'react';
import Home from './components/Home';
import Presentation from './components/Presentation';
import Loading from './components/Loading'; // Importa el componente Loading

function App() {
  const [page, setPage] = useState('loading'); // Establece la página inicial en 'loading'

  useEffect(() => {
    // Simula una carga de la aplicación
    const timer = setTimeout(() => {
      setPage('home');
      // Cambia a la página principal después de 2 segundos (ajusta según sea necesario)
    }, 2000);

    return () => clearTimeout(timer)
  }, []);


    // Funciones para cambiar de página
    const goToPresentation = () => {
      setPage('presentation');
    };
  
    const goToHome = () => {
      setPage('home');
    };

  return (
    <div>
      {page === 'loading' && <Loading />} {/* Muestra la página de carga inicial */}
      {page === 'home' && <Home />}
      {page === 'presentation' && <Presentation />}
    </div>
  );
}

export default App;
