import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../css/login.css'; 

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate(); // Usa useNavigate para la navegación


  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // console.log('Submitting login with:', { email, password });
      const response = await axios.post ('http://localhost:3000/login', { email, password }); 
       
      const { token } = response.data;
      localStorage.setItem('token', token);
      const decodedToken = JSON.parse(atob(token.split('.')[1]));
      navigate(`/perfil/${decodedToken.userId}`); // Utiliza navigate para redirigir
    } catch (error) {
      console.error('Error:', error);
      setError('An error occurred. Please try again.');
    }
  };

  return (
    <div>
      <div className='Title'>
        <h1>Alliance</h1>
      </div>
      <div className='Body'>
        <div className='Inicio'><h1>Iniciar Sesión</h1></div>
        <form onSubmit={handleSubmit}>
          <div className='email'>
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={handleEmailChange}
              required
            />
          </div>
          <div>
            <label htmlFor="password">Contraseña </label> 
            <input
              type="password"
              id="password"
              value={password}
              onChange={handlePasswordChange}
              required
            />
          </div>
          {error && <p className="error">{error}</p>}
          <div className='ButtonAcces'>
            <button type="submit" className='InitSes'>Iniciar sesión</button>
          </div>
        </form>
      </div>
      <div className='Access'><h2>¿Olvidaste la contraceña?</h2>
        <div className='ButtonAcces'>
          <button onClick={() => navigate('/registro')} className='InitAccess' >¿Primera vez en Alliance? Accede ahora</button>
          </div>
      </div>
    </div>
  );
}

export default Login;
