import React, { useState, useEffect } from 'react';
import Home from './components/Login';
import Presentation from './components/Presentation';
import Loading from './components/Loading';
import Logprov from './components/Logprov';

function App() {
  const [page, setPage] = useState('loading');

  useEffect(() => {
    const timer = setTimeout(() => {
      setPage('login');
    }, 2000);
    // despues de la pantalla de carga, se dirige directamente al home

    return () => clearTimeout(timer)
  }, []);
  // esta funcion return, corta el tiempo de la presentacion

  const goToPresentation = () => {
    setPage('presentation');
  };

  const goToHome = () => {
    setPage('login');
  };

  // const goToLogprov= () => {
  //   setPage('logprov');
  // };

  return (
    <div>
      {page === 'loading' && <Loading />}
      {page === 'login' && <Home goToPresentation={goToPresentation} />} 
      {/* EL gotopresentation se utiliza para la funcion del boton, lo mismo con go to home. */}
      {page === 'presentation' && <Presentation goToHome={goToHome} />}
      {/* {page === 'logprov' && <Presentation goToLogprov={goToLogprov} />} */}
    </div>
  );
}

export default App;
